import React,{useState}  from 'react'; 

import { View,Button,Text,StyleSheet,TextInput ,TouchableOpacity } from "react-native"; 

import DetailsScreen from './DetailsScreen'; 

const  AddPetscreen = ({navigation}) => { 

    const [name,addName]=useState(''); 

    const [email,addEmail]=useState(''); 

    const [mobnum,addMobNum]=useState(''); 

    const [empid,addEmpID]=useState(''); 

   const [users,addUser]=useState([]); 

 

   const handleName = text =>{ 

       addName(text) 

   } 

   const handleEmpID = text =>{ 

    addEmpID(text) 

  } 

   const handleEmail = text =>{ 

    addEmail(text) 

} 

const handleNumber = text =>{ 

  addMobNum(text) 

} 

 

   const handleClick = () =>{ 

       const usersData=users; 

       usersData.push(name,empid,email,mobnum); 

       addUser(usersData); 

       addName(''); 

       addEmpID(''); 

       addEmail(''); 

       addMobNum(''); 

       

      } 

    return ( 

        <View style={styles.container}> 

          <TextInput  

            style={styles.textbox} 

            onChangeText={handleName} 

              value={name} 

              placeholder='enter full name' 

          /> 

            <TextInput  

            style={styles.textbox} 

            onChangeText={handleEmpID} 

              value={empid} 

              placeholder='enter employeeID' 

          /> 

               <TextInput  

            style={styles.textbox} 

            onChangeText={handleEmail} 

              value={email} 

              placeholder='enter email' 

          /> 

                <TextInput  

            style={styles.textbox} 

            onChangeText={handleNumber} 

              value={mobnum} 

              placeholder='enter mobilenumber' 

          /> 

             {/* <Button  

        

                 style={{fontSize: 20, color: 'green'}}  

                   title="Add Me" 

                   onPress={handleClick} 

            /> 

            <br /> */} 

 

            <TouchableOpacity  onPress={handleClick}> 

              <Text style = {{fontSize:23, 

                 backgroundColor:'#1e90ff', 

                 color:'white', 

                 marginBottom:70, 

                 textAlign:'center', 

                 padding:8, 

                 borderRadius:5, 

                 height:48}}> 

                    SAVE 

             </Text> 

              </TouchableOpacity> 

             

              <br /> 

      

            <Button color='red' 

          title="Check Employee Details" 

          onPress={() => navigation.navigate( 

                     'DetailsScreen', 

                      { 

                        details: users.map((user,index)=>( 

                          <View> 

                            <Text key={index}  

                                  style={{fontSize:20,color:'#008b8b'}}> 

                                    <ul>{user}</ul> 

                                    </Text> 

                          </View> 

                          ) 

                        ) 

                         

                      }                 )} 

        />  

 

            

     

        </View> 

    ); 

} 

 

const styles=StyleSheet.create({ 

    container:{ 

        marginTop:12, 

        padding:40 

    }, 

    textbox: 

    { 

      fontSize:20, 

      width:280, 

      height:50, 

      borderColor:'#a9a9a9', 

      borderWidth:3, 

      borderRadius:5, 

      borderBottomColor: 'black', 

      marginBottom:30 

    } 

 

}) 

export default AddPetscreen;
