import React, { useEffect, useState } from 'react'; 

import { Text, View,ScrollView,Image ,StyleSheet} from 'react-native'; 

 

const PetfitDetails = () => 

{ 

  const [data, setData] = useState([]); 

 

  useEffect(() => { 

    fetch('https://jsonblob.com/api/0d1cca4e-1b56-11eb-9893-13f2d53c2bf9') 

      .then((response) => response.json()) 

      .then((json) => setData(json.pets)) 

  }, []); 

 

  return ( 

    <ScrollView style={{width:'97%',marginLeft:7}}> 

    {data.map((info, index) => ( 

      <View key={index} style={styles.datapart}> 

         <Image  

          style={{ width: 100, height: 100 }} 

          source={{uri:info.images}} 

        /> 

        <View style={{ flexDirection:'column',marginLeft:20,}}> 

            <Text style={styles.textpart}> 

               Pet Name: {info.petname} 

            </Text> 

            <Text style={styles.textpart}> 

              Pet Age: {info.petage} 

            </Text> 

            <Text style={styles.textpart}> 

               Pet Location: {info.petlocation} 

            </Text> 

            <Text style={styles.textpart}> 

               Pet Breed: {info.petbreed} 

            </Text> 

            </View> 

      </View> 

    ))} 

 

  </ScrollView>  

 

  ); 

} 

 

export default PetfitDetails; 

 

const styles=StyleSheet.create({ 

    datapart:{ 

        backgroundColor:'#a9a9a9', 

        flexDirection:'row', 

        padding:15, 

        marginTop:9, 

        height:130 

    }, 

    textpart:{ 

        fontSize:19, 

        fontWeight:400, 

        fontFamily:'Calibri' 

    } 

})
